INSERT INTO `vote_activities` VALUES ('422542759958949888', '肇庆新区跨年嘉年华', '嘉年华活动围绕肇庆山湖城江自然美景，拥抱粤港澳大湾区，振兴肇庆旅游产业为核心，讲述七星肇庆的城市故事，呈现肇庆独特的人文风俗。', b'0', NULL, NULL, b'0', '2020-02-05 21:15:10', 'Hocassian', b'0', '2020-02-07 18:18:47', NULL);
INSERT INTO `vote_activities` VALUES ('423223787262324736', '百辆房车游肇庆', '邀请全国车友自驾房车走进肇庆，体验千年肇庆的历史文化及优秀旅游资源，更有众多年味节目及特色礼品，欢迎国内外车友踊跃报名。', b'0', NULL, NULL, b'0', '2020-02-07 18:21:20', NULL, b'0', '2020-02-07 18:21:20', NULL);
INSERT INTO `vote_activities` VALUES ('450657498811346944', '论文答辩', '肇庆学院计算机软件学院2020届毕业生何承翰同学的论文答辩，他本次答辩的项目为“基于SpringBoot的新媒体企业信息化系统设计与实现”，该系统主要由五大功能模块组成：官网、小程序、管理后台、综控后端和分发后端。其中，官网用于公司的招商引流和品牌建设；小程序用于活动的直播与和观众的互动；管理后台则为员工与管理员提供管理官网、小程序、和分发内容的服务；综控后端收到用户在管理后台发出的请求后，对各项内容逐一做出响应；若该请求包含内容的分发，则通过消息中间件依次推送给分发后端逐个自动分发。', b'1', 'https://js.live-play.acgvideo.com/live-js/607258/live_2303936_4137514.flv?wsSecret=4b6d6e175b9604a8fa14813517db8564&wsTime=1588058473&trid=ae116a15a39940c9aefd413263c12f17&pt=web&oi=455824913&order=0&sig=no&platform=web&pSession=D9tCDFbW-c0iB-40kw-BB1Z-bmesca3TdGAy', '453061337002815488.jpg', b'0', '2020-04-23 11:13:06', 'Hocassian', b'0', '2020-04-30 02:25:06', 'Hocassian');
